#class of tweet data parameters
class tweet_data:
    def __init__(self, time_stamp, user, text):
        self.time_stamp = time_stamp
        self.user = user
        self.text = text
    def details(self):
        print(f"{self.time_stamp} {self.user} {self.text}")

